class GraphQlTests {

    @Test
    fun testFragmentsOperationSerialization() {
        val operation = Operations.CountryCodeQuery("AD")
        assertEquals(fragmentsQuery, operation.jsonString)
    }


    @Test
    fun testSimpleQuerySerialization() {
        assertEquals(simpleQuery, Operations.Countries().jsonString)
    }


    @Test
    fun testSimpleResponse() {
        try {
            Json.decodeFromString(GraphQlResponse.serializer(Operations.Countries().responseSerializer), simpleResponse)
        } catch (e: Exception) {
            assertNull(e)
        }
    }

    @Test

    fun testFragmentsResponse() {
        try {
            Json.decodeFromString(
                GraphQlResponse.serializer(Operations.CountryCodeQuery("AD").responseSerializer),
                fragmentsResponse
            )
        } catch (e: Exception) {
            assertNull(e)
        }
    }
}